mapper.select:
/** selectBoardList **/
SELECT
    *
FROM board t1
JOIN user t2 ON t1.user_id = t2.id
WHERE t1.id = #{id}

mapper.insert:
insert into board(
    user_id
    , title
    , content
    , create_date
)Value(
    #{userId}
    , #{title}
    , #{content}
    , NOW()
)

mapper.update:
UPDATE board SET
    title = #{title}
    , content = #{content}
    , update_id = #{updateId}
    , update_date = NOW()
WHERE id = #{id}
