mapper.select:
/** selectFileList **/
SELECT
    *
FROM file t1
JOIN user t2 ON t1.user_id = t2.id
WHERE t1.id = #{id}

mapper.insert:
insert into file(
    division
    , ref_id
    , system_filename
    , original_filename
    , filepath
    , create_date
)Value(
    #{division}
    , #{refId}
    , #{systemFilename}
    , #{originalFilename}
    , #{filepath}
    , NOW()
)
