mapper.select:
/** selectFile **/
SELECT
    *
FROM file t1
WHERE id = #{id}

/** selectFileList **/
SELECT
    *
FROM file t1
JOIN user t2 ON t1.ref_id = t2.id
WHERE t1.ref_id = #{refId}

    AND t1.boss_type = #{bossType}


    AND t1.flag = #{flag}

mapper.insert:
insert into file(
    division
    , ref_id
    , system_filename
    , original_filename
    , filepath
    , image_name
    , flag
    , create_date
)Value(
    #{division}
    , #{refId}
    , #{systemFilename}
    , #{originalFilename}
    , #{filepath}
    , #{imageName}
    , #{flag}
    , NOW()
)
